import React from 'react';
import { Consumer } from './Context';

const Stats = () => {

  return (
    <Consumer>
      { context => {
        const totalPlayers = context.length;
        const totalPoints = context.reduce( (total, players) => {
          return total + players.score;
        }, 0);

        return (
          <table className="stats">
            <tbody>
              <tr>
                <td>Players:</td>
                <td>{ totalPlayers }</td>
              </tr>
              <tr>
                <td>Total Points:</td>
                <td>{ totalPoints }</td>
              </tr>
            </tbody>
          </table>
        );
      }}
     </Consumer>
  );
}

export default Stats;
